/* UAT_XUSTG_ART */ SELECT UserActyLogs.*, USERTAGs.*, Articles.*, PA_ACT.pdaccid act_uid, PA_ACT.pdacc_eid act_ueid, PA_ACT.pdacc_ufn act_ufn, PA_ACT.pdacc_upsd act_upsd, PA_TAR.pdaccid tag_uid, PA_TAR.pdacc_eid tag_ueid, PA_TAR.pdacc_ufn tag_ufn, PA_TAR.pdacc_upsd tag_upsd
FROM UAL_ActionTypes, UserActyLogs INNER JOIN USTGs_ARTICLE ON ustgart_ustgid = ualg_refobj INNER JOIN USERTAGs ON ustg_id = ustgart_ustgid INNER JOIN Articles ON ustgart_artid = artid
INNER JOIN Proddb_Accounts PA_ACT ON PA_ACT.pdaccid = art_accid /* TARGET : Celui qui est marqué sur ARTICLE */ INNER JOIN Proddb_Accounts PA_TAR ON PA_TAR.pdaccid = ustg_tgtuid
WHERE uat_id = ualg_uatid
AND PA_ACT.pdacc_todelete = 0 AND PA_TAR.pdacc_todelete = 0
/* CONTRAINTES : ACTOR n'est pas le MARQUÉ */ AND ustg_tgtuid != ualg_actuid /* CONTRAINTES : Le MARQUÉ n'est pas ART_OWNER */ AND art_accid != ustg_tgtuid
AND ualgid NOT IN ( SELECT pmrptem_ualgid FROM Postman_Reports_Email WHERE pmrptem_rcptuid = PA_TAR.pdaccid ) AND uat_id IN (1101) AND ualgid = :uaid;

/* UAT_XUSTG_RCT */ SELECT UserActyLogs.*, Reactions.*, Articles.*, USERTAGs.*, PA_ACT.pdaccid act_uid, PA_ACT.pdacc_eid act_ueid, PA_ACT.pdacc_ufn act_ufn, PA_ACT.pdacc_upsd act_upsd, PA_TAR.pdaccid tag_uid, PA_TAR.pdacc_eid tag_ueid, PA_TAR.pdacc_ufn tag_ufn, PA_TAR.pdacc_upsd tag_upsd
FROM UAL_ActionTypes, UserActyLogs INNER JOIN USTGs_REACTION ON ustgrct_ustgid = ualg_refobj INNER JOIN USERTAGs ON ustg_id = ustgrct_ustgid INNER JOIN Reactions ON ustgrct_rctid = reactid INNER JOIN Articles ON react_artid = artid
INNER JOIN Proddb_Accounts PA_ACT ON PA_ACT.pdaccid = react_writer /* TARGET : Le MARQUÉ sur REACTION */ INNER JOIN Proddb_Accounts PA_TAR ON PA_TAR.pdaccid = ustg_tgtuid
WHERE uat_id = ualg_uatid
AND PA_ACT.pdacc_todelete = 0 AND PA_TAR.pdacc_todelete = 0
/* CONTRAINTES : Le MARQUÉ n'est pas ACTOR */ AND ustg_tgtuid != ualg_actuid /* CONTRAINTES : Le MARQUÉ n'est pas ART_OWNER */ AND art_accid != ustg_tgtuid
AND ualgid NOT IN ( SELECT pmrptem_ualgid FROM Postman_Reports_Email WHERE pmrptem_rcptuid = PA_TAR.pdaccid ) AND uat_id IN (1102) AND ualgid = :uaid;

