FROM UAL_ActionTypes, UserActyLogs INNER JOIN USTGs_TESTY ON ustgtst_ustgid = ualg_refobj INNER JOIN USERTAGs ON ustg_id = ustgtst_ustgid INNER JOIN TESTIES ON ustgtst_tstid = tstid
