SELECT * FROM ( ( SELECT artid, art_eid, art_picid, art_pic_rpath, art_desc, art_crea_tstamp, art_locip, NULL art_trid, NULL art_trd_eid, NULL art_trd_title, NULL art_trd_desc, NULL art_trd_title_href, NULL art_trd_catgid, NULL art_trd_is_public, NULL art_trd_grat, NULL art_trd_date_tstamp, art_hashs, art_rnb, art_me, art_evals, art_tot, art_oid, art_ogid, art_oeid, art_opsd, art_ofn, art_oppicid, art_oppic_rpath, art_todel, art_state, art_is_sod, art_vid_url FROM VM_ARTICLES_IML ) UNION ( SELECT * FROM VM_ARTICLES_ITR ) ) AS U /* COMMON CONSTRAINTS : ALGO */ WHERE ( ( U.art_tot >= :must_lnb AND U.art_rnb >= :must_rnb ) OR ( /* COMMON CONSTRAINTS : MUST INCLUDE ARID */ U.artid IN ( SELECT eainc_refid FROM explorer_article_include WHERE eainc_type IN (1,2) AND eainc_dend_tstamp IS NULL ) /* COMMON CONSTRAINTS : MUST EXCLUDE ARID */ AND U.artid NOT IN ( SELECT eaexc_refid FROM explorer_article_exclude WHERE eaexc_type IN (1,2) AND eaexc_dend_tstamp IS NULL )
/* COMMON CONSTRAINTS : MUST INCLUDE (from) UID */ AND U.art_oid IN ( SELECT eainc_refid FROM explorer_article_include WHERE eainc_type IN (5) AND eainc_dend_tstamp IS NULL ) /* COMMON CONSTRAINTS : MUST EXCLUDE (from) UID */ AND U.art_oid NOT IN ( SELECT eaexc_refid FROM explorer_article_exclude WHERE eaexc_type IN (5) AND eaexc_dend_tstamp IS NULL )
/* COMMON CONSTRAINTS : MUST INCLUDE (from) TRID */ AND art_trid IN ( SELECT eainc_refid FROM explorer_article_include WHERE eainc_type IN (4) AND eainc_dend_tstamp IS NULL ) /* COMMON CONSTRAINTS : MUST EXCLUDE (from) TRID */ AND art_trid NOT IN ( SELECT eaexc_refid FROM explorer_article_exclude WHERE eaexc_type IN (4) AND eaexc_dend_tstamp IS NULL ) ) ) AND ( U.art_is_sod = 1 OR U.art_trid IS NOT NULL ) AND U.art_todel = 0 ORDER BY U.art_crea_tstamp DESC LIMIT :limit ;

SELECT * FROM ( ( SELECT artid, art_eid, art_picid, art_pic_rpath, art_desc, art_crea_tstamp, art_locip, NULL art_trid, NULL art_trd_eid, NULL art_trd_title, NULL art_trd_desc, NULL art_trd_title_href, NULL art_trd_catgid, NULL art_trd_is_public, NULL art_trd_grat, NULL art_trd_date_tstamp, art_hashs, art_rnb, art_me, art_evals, art_tot, art_oid, art_ogid, art_oeid, art_opsd, art_ofn, art_oppicid, art_oppic_rpath, art_todel, art_state, art_is_sod, art_vid_url FROM VM_ARTICLES_IML ) UNION ( SELECT * FROM VM_ARTICLES_ITR ) ) AS U /* COMMON CONSTRAINTS : ALGO */ WHERE ( ( U.art_tot >= :must_lnb AND U.art_rnb >= :must_rnb ) OR ( /* COMMON CONSTRAINTS : MUST INCLUDE ARID */ U.artid IN ( SELECT eainc_refid FROM explorer_article_include WHERE eainc_type IN (1,2) AND eainc_dend_tstamp IS NULL ) /* COMMON CONSTRAINTS : MUST EXCLUDE ARID */ AND U.artid NOT IN ( SELECT eaexc_refid FROM explorer_article_exclude WHERE eaexc_type IN (1,2) AND eaexc_dend_tstamp IS NULL )
/* COMMON CONSTRAINTS : MUST INCLUDE (from) UID */ AND U.art_oid IN ( SELECT eainc_refid FROM explorer_article_include WHERE eainc_type IN (5) AND eainc_dend_tstamp IS NULL ) /* COMMON CONSTRAINTS : MUST EXCLUDE (from) UID */ AND U.art_oid NOT IN ( SELECT eaexc_refid FROM explorer_article_exclude WHERE eaexc_type IN (5) AND eaexc_dend_tstamp IS NULL )
/* COMMON CONSTRAINTS : MUST INCLUDE (from) TRID */ AND art_trid IN ( SELECT eainc_refid FROM explorer_article_include WHERE eainc_type IN (4) AND eainc_dend_tstamp IS NULL ) /* COMMON CONSTRAINTS : MUST EXCLUDE (from) TRID */ AND art_trid NOT IN ( SELECT eaexc_refid FROM explorer_article_exclude WHERE eaexc_type IN (4) AND eaexc_dend_tstamp IS NULL ) ) ) AND ( U.art_is_sod = 1 OR U.art_trid IS NOT NULL ) AND U.art_eid != :refid AND U.art_crea_tstamp <= :reftm AND U.art_todel = 0 ORDER BY U.art_crea_tstamp DESC LIMIT :limit ;

