app.controller("ArticleController",['$scope','$http','$filter','ArticleFactory',function($scope, $http, $filter, ArticleFactory) {

	var articles = [];

	/**************************************************************************************************************/
	/************************************************ ACTIONS SCOPE ***********************************************/							
	/**************************************************************************************************************/

	$scope.AddAction = function(){

	};

	$scope.DeleteAction = function(){

	};

	$scope.ListAction = function(){

	};


	/**************************************************************************************************************/
	/************************************************ PRIVATE SCOPE ***********************************************/							
	/**************************************************************************************************************/

	var _f_displaytArticles = function (articles) {
		$scope.articles = articles;
	};

	/**************************************************************************************************************/
	/************************************************* DATAS SCOPE ************************************************/							
	/**************************************************************************************************************/

	var _fDs_autoLoadArticles = function () {
		
		ArticleFactory.getArticles().then(function(datas) {
			$scope.articles = datas;
		}, function(reason) {
		  alert('Failed: ' + reason);
		}, function(update) {
		  console.log('Got notification: ' + update);
		});

	};


	/*********************************************************************************************************/
	/****************************************** WATCH & BIND SCOPE *******************************************/							
	/*********************************************************************************************************/

	$scope.$watch("articles",function(){
		
	},true);


	/*********************************************************************************************************/
	/*********************************************** INIT SCOPE **********************************************/							
	/*********************************************************************************************************/

	(function(){
		_fDs_autoLoadArticles();
	})();
	
}]);